import React, { Component } from "react";
import * as moment from "moment";
import DateRangePicker from "react-bootstrap-daterangepicker";

class Booking extends Component {
  constructor(props) {
    super(props);

    this.bookedOutDates = [];
  }

  componentDidMount() {
    this.getBookedOutDates();
  }

  getBookedOutDates = () => {
    const bookings = this.props.rental;
    if (bookings && bookings.length > 0) {
      bookings.map(booking => {
        const dateRange = this.getRangeOfDates(
          booking.startAt,
          booking.endAt,
          "Y/MM/DD"
        );
        debugger;
        this.bookedOutDates.push(...dateRange);
        console.log(this.bookedOutDates);
      });
    }
  };

  getRangeOfDates = (startAt, endAt, dateFormat = "Y/MM/DD") => {
    const tempDates = [];

    const mEndAt = moment(endAt);
    let mStartAt = moment(startAt);

    while (mStartAt < mEndAt) {
      //convert start date format with moment
      tempDates.push(mStartAt.format(dateFormat));
      mStartAt = mStartAt.add(1, "day");
    }

    //convert end date format with moment
    tempDates.push(mEndAt.format(dateFormat));

    return tempDates;
  };

  render() {
    const { rental } = this.props;
    return (
      <div className="booking">
        <h3 className="booking-price">
          <span className="booking-per-night">
            <span className="price"> ${rental.dailyRate} </span>per night
          </span>
        </h3>
        <hr></hr>
        <div className="form-group">
          <label htmlFor="dates">Dates</label>
          <DateRangePicker opens="left" containerStyles={{ display: "block" }}>
            <input id="dates" type="text" className="form-control"></input>
          </DateRangePicker>
        </div>
        <div className="form-group">
          <label htmlFor="guests">Guests</label>
          <input
            type="number"
            className="form-control"
            id="guests"
            aria-describedby="guests"
            placeholder=""
          />
        </div>
        <button className="btn btn-rental btn-confirm btn-block">
          Reserve
        </button>
        <hr></hr>
        <p className="booking-note-title">
          People are interested into this house
        </p>
        <p className="booking-note-text">
          More than 500 people checked this rental in last month.
        </p>
      </div>
    );
  }
}

export default Booking;
