import { FETCH_RENTALS, FETCH_RENTALS_BY_ID } from "../actions/types";

const initialState = {
  rentals: [
    // {
    //   id: 1,
    //   title: "testing"
    // }
  ]
  // rental: {}
};

//reducer for rentals
export const rentals = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_RENTALS:
      return { ...state, rentals: action.rentals };
    default:
      return state;
  }
};

//reducer for a rental
export const rental = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_RENTALS_BY_ID:
      return { ...state, rental: action.rental };
    default:
      return state;
  }
};
