import React, { Component } from "react";
import { withRouter } from "react-router-dom";

class RentalSearchInput extends Component {
  constructor(props) {
    super(props);

    this.searchInput = React.createRef();
    this.state = {};
    this.handleSearch = this.handleSearch.bind(this);
  }

  handleKeyPress(e) {
    if (e.key === "Enter") {
      this.handleSearch();
    }
  }
  handleSearch = () => {
    const { history } = this.props;
    const city = this.searchInput.current.value;

    city ? history.push(`/rentals/${city}/homes`) : history.push("/rentals");
    console.log("here");
  };

  render() {
    return (
      // <div>
      //   <input
      //     ref={this.searchInput}
      //     type="search"
      //     placeholder="Where do you want to go?"
      //   />
      //   <button onClick={this.handleSearch} type="submit" value="Search" />
      // </div>

      <div className="form-inline my-2 my-lg-0">
        <input
          onKeyPress={event => {
            this.handleKeyPress(event);
          }}
          ref={this.searchInput}
          className="form-control mr-sm-2 bwm-search"
          type="search"
          placeholder='Try "New York"'
          aria-label="Search"
        ></input>
        <button
          onClick={() => {
            this.handleSearch();
          }}
          className="btn btn-outline-success my-2 my-sm-0 btn-bwm-search"
          type="submit"
        >
          Search
        </button>
      </div>
    );
  }
}

export default withRouter(RentalSearchInput);
