import React, { Component } from "react";
import { Link } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";

import { fetchUserRentals, deleteRental } from "../../actions/";
import { connect } from "react-redux";

import RentalManageCard from "./RentalManageCard";

class RentalManage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      userRentals: [],
      errors: [],
      isFetching: false
    };

    this.deleteRental = this.deleteRental.bind(this);
  }

  componentDidMount() {
    this.setState({ isFetching: true });
    this.props.fetchUserRentals();
    this.setState({ isFetching: false });
  }

  deleteRental = (rentalId, rentalIndex) => {
    this.props
      .deleteRental(rentalId)
      .then(() => {
        // this.setState({
        //   userRentals: this.state.userRentals.splice(1, rentalIndex)
        // });
        this.deleteRentalFromList(rentalIndex);
        toast.success("Rental has been succesfully deleted!");
        this.props.history.push("/");
      })
      .catch(error => console.log(error));
  };

  deleteRentalFromList(rentalIndex) {
    const userRentals = this.state.userRentals.slice();
    userRentals.splice(rentalIndex, 1);

    this.setState({ userRentals });
  }

  render() {
    const { isAuthenticated } = this.props.auth;
    const { isFetching } = this.state;
    const { rentals } = this.props;

    return (
      <div>
        {isAuthenticated && (
          <section id="userRentals">
            <ToastContainer />
            <h1 className="page-title">My Rentals</h1>
            <div className="row">
              {rentals.map((rental, index) => (
                <RentalManageCard
                  key={index}
                  rental={rental}
                  rentalIndex={index}
                  deleteRentalCb={this.deleteRental}
                />
              ))}
              ;
            </div>

            {!isFetching && rentals.length === 0 && (
              <div className="alert alert-warning">
                You dont have any rentals currenty created. If you want
                advertised your property please follow this link.
                <Link
                  style={{ marginLeft: "10px" }}
                  className="btn"
                  to="/rentals/create"
                >
                  Register Rental
                </Link>
              </div>
            )}
          </section>
        )}
      </div>
    );
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
  rentals: state.rentals.rentals
});

export default connect(mapStateToProps, {
  fetchUserRentals,
  deleteRental
})(RentalManage);
