import { FETCH_RENTALS, FETCH_RENTALS_BY_ID } from "../actions/types";

const initialRentalsState = {
  rentals: []
};

const initialRentalState = {
  rental: []
};

//reducer for rentals
export const rentals = (state = initialRentalsState, action) => {
  switch (action.type) {
    case FETCH_RENTALS:
      // return { ...state, rentals: action.rentals };
      // debugger;
      return { ...state, rentals: action.payload };
    default:
      return state;
  }
};

//reducer for a rental
export const rental = (state = initialRentalState, action) => {
  switch (action.type) {
    case FETCH_RENTALS_BY_ID:
      // return { ...state, rental: action.rental };
      return { ...state, payload: action.payload };
    default:
      return state;
  }
};
