import * as jwt from "jsonwebtoken";

class authService {
  tokenKey = "x-auth-token";

  getToken = () => {
    return localStorage.getItem(this.tokenKey);
  };

  decode = token => {
    return jwt.decode(token);
  };

  getUsername = () => {
    return this.decode(this.getToken()).username;
  };
}

export default new authService();
