import React, { Component } from "react";
import { getRangeOfDates } from "../../helpers";
import DateRangePicker from "react-bootstrap-daterangepicker";
import * as moment from "moment";

class Booking extends Component {
  constructor() {
    super();

    this.bookedOutDates = [];
    this.state = {
      startAt: "",
      endAt: "",
      guests: 0
    };
  }

  componentWillMount() {
    this.getBookedOutDates();
  }

  getBookedOutDates() {
    const { bookings } = this.props.rental;

    if (bookings && bookings.length > 0) {
      bookings.forEach(booking => {
        const dateRange = getRangeOfDates(
          booking.startAt,
          booking.endAt,
          "Y/MM/DD"
        );

        this.bookedOutDates.push(...dateRange);
      });
    }
  }

  //check if the bookedOutDates includes the current date and check if the date is not a past date
  handleInvalidDates = date => {
    return (
      this.bookedOutDates.includes(date.format("Y/MM/DD")) ||
      date.diff(moment(), "days") < 0
    );
  };

  handleEvent(event, picker) {
    const startAt = picker.startDate.format("Y/MM/DD");
    const endAt = picker.endDate.format("Y/MM/DD");

    this.setState({ startAt: startAt, endAt: endAt });
    console.log(this.state);
  }

  render() {
    const { rental } = this.props;
    return (
      <div className="booking">
        <h3 className="booking-price">
          <span className="booking-per-night">
            <span className="price"> ${rental.dailyRate} </span>per night
          </span>
        </h3>
        <hr></hr>
        <div className="form-group">
          <label htmlFor="dates">Dates</label>
          <DateRangePicker
            isInvalidDate={this.handleInvalidDates}
            onEvent={this.handleEvent}
            opens="left"
            containerStyles={{ display: "block" }}
          >
            <input id="dates" type="text" className="form-control"></input>
          </DateRangePicker>
        </div>
        <div className="form-group">
          <label htmlFor="guests">Guests</label>
          <input
            type="number"
            className="form-control"
            id="guests"
            aria-describedby="guests"
            placeholder=""
          />
        </div>
        <button className="btn btn-rental btn-confirm btn-block">
          Reserve
        </button>
        <hr></hr>
        <p className="booking-note-title">
          People are interested into this house
        </p>
        <p className="booking-note-text">
          More than 500 people checked this rental in last month.
        </p>
      </div>
    );
  }
}

export default Booking;
