import React, { Component } from "react";
import { fetchUserBookings } from "../../actions";

import { connect } from "react-redux";

class BookingManage extends Component {
  componentDidMount() {
    this.props.fetchUserBookings();
  }
  render() {
    const { isAuthenticated } = this.props.auth;
    const { bookings } = this.props;
    return (
      <section id="userBookings">
        <h1 className="page-title">My Bookings</h1>
        <div className="row">
          {isAuthenticated && (
            <div>
              {bookings.map(booking => (
                //   <p key={booking._id}>
                //   {booking.startAt} - {booking.endAt}
                // </p>
                <div className="col-md-4">
                  <div className="card text-center">
                    <div className="card-header">Rental Category</div>
                    <div className="card-block">
                      <h4 className="card-title">
                        {" "}
                        Rental Title - Rental City
                      </h4>
                      <p className="card-text booking-desc">
                        Rental Description
                      </p>
                      <p className="card-text booking-days">
                        {booking.startAt} - {booking.endAt} | {booking.days}{" "}
                        days
                      </p>
                      <p className="card-text booking-price">
                        <span>Price: </span>{" "}
                        <span className="booking-price-value">
                          {booking.totalPrice} $
                        </span>
                      </p>
                      <Link className="btn btn-bwm" to="/">
                        Go to Rental
                      </Link>
                    </div>
                    <div className="card-footer text-muted">
                      {booking.createdAt}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
        <div class="alert alert-warning">
          You have no bookings created go to rentals section and book your place
          today.
          <Link
            style={{ "margin-left": "10px" }}
            class="btn btn-bwm"
            to="rentals index page"
          >
            Available Rental
          </Link>
        </div>
      </section>
    );
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
  bookings: state.bookings.bookings,
  errors: state.errors
});

export default connect(mapStateToProps, { fetchUserBookings })(BookingManage);
