import { FETCH_RENTALS, FETCH_RENTALS_BY_ID } from "./types";

const rentals = [
  {
    id: 1,
    title: "testing"
  },
  {
    id: 2,
    title: "oto"
  },
  {
    id: 3,
    title: "toto"
  }
];

export const fetchRentals = () => async dispatch => {
  //   return {
  //     type: FETCH_RENTALS,
  //     rentals
  //   };
  dispatch({ type: FETCH_RENTALS, rentals });
};

export const fetchRentalsById = rentalId => async dispatch => {
  const rental = rentals.find(rental => rental.id === Number(rentalId));
  dispatch({ type: FETCH_RENTALS_BY_ID, rental });
};
