import React from "react";
import { Field, reduxForm } from "redux-form";
import { renderField } from "./shared/form/input";

const SignupForm = props => {
  const { handleSubmit, pristine, reset, submitting, submitCb, valid } = props;
  console.log(valid);
  return (
    <form onSubmit={handleSubmit(submitCb)}>
      <div>
        <Field
          label="Username"
          name="username"
          component={renderField}
          type="text"
          placeholder="Username"
          className="form-control"
        />
      </div>

      <div>
        <Field
          label="Email"
          name="email"
          component={renderField}
          type="email"
          placeholder="Email address"
          className="form-control"
        />
      </div>

      <div>
        <Field
          label="Password"
          name="password"
          component={renderField}
          type="password"
          placeholder="Password"
          className="form-control"
        />
      </div>

      <div>
        <Field
          label="Password Confirmation"
          name="passwordConfirmation"
          component={renderField}
          type="password"
          placeholder="Password confirmation"
          className="form-control"
        />
      </div>

      <div>
        <button className="btn" type="submit" disabled={!valid}>
          Submit
        </button>
      </div>
    </form>
  );
};

const validate = values => {
  const errors = {};
  if (!values.username) {
    errors.username = "Required";
  } else if (values.username.length < 4) {
    errors.username = "Must be at least 4 characters or more";
  }

  if (!values.email) {
    errors.email = "Required";
  } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
    errors.email = "Invalid email address";
  }
  if (!values.passwordConfirmation) {
    errors.password = "Required";
  } else if (values.password !== values.passwordConfirmation) {
    errors.password = "Passwords must be the same";
  }
  return errors;
};

export default reduxForm({
  form: "signupForm", // a unique identifier for this form
  validate
})(SignupForm);
