import React, { Component } from "react";
import { withRouter } from "react-router-dom";

class RentalSearchInput extends Component {
  constructor(props) {
    super(props);

    this.searchInput = React.createRef();
    this.state = {};
    this.handleSearch = this.handleSearch.bind(this);
  }

  handleKeyPress(e) {
    if (e.key === "Enter") {
      this.handleSearch();
    }
  }
  handleSearch = () => {
    const { history } = this.props;
    const city = this.searchInput.current.value;

    city ? history.push(`/rentals/${city}/homes`) : history.push("/rentals");
    console.log("here");
  };

  render() {
    return (
      <div>
        <input
          onKeyPress={e => {
            this.handleKeyPress(e);
          }}
          ref={this.searchInput}
          className="input"
          type="search"
          placeholder="Where do you want to go?"
        />
        <input onClick={this.handleSearch} type="submit" value="Search" />
      </div>
    );
  }
}

export default withRouter(RentalSearchInput);
