import React, { Component } from "react";
import { connect } from "react-redux";
import { createRental } from "../../actions/";
import TextFieldGroup from "../common/TextFieldGroup";

export class RentalForm extends Component {
  constructor() {
    super();
    this.state = {
      title: "",
      subtitle: "",
      street: "",
      city: "",
      country: "",
      image: "",
      category: "",
      bedrooms: "",
      guests: "",
      beds: "",
      bathrooms: "",
      shared: true,
      cleanTitle: "",
      cleanDetails: "",
      checkInTitle: "",
      checkInDetails: "",
      description: "",
      spaceDescription: "",
      guestsAccessDescription: "",
      amenities: [],
      sleepingArrangements: "",
      dailyRate: "",
      errors: {},
      redirect: false
    };
    this.onSubmit = this.onSubmit.bind(this);
    this.onChange = this.onChange.bind(this);
  }

  componentDidMount() {
    if (this.state.redirect) {
      this.props.history.push("/");
    }
  }

  onSubmit = e => {
    try {
      this.setState({ errors: {} });
      const {
        title,
        subtitle,
        street,
        city,
        country,
        image,
        category,
        bedrooms,
        guests,
        beds,
        bathrooms,
        shared,
        cleanTitle,
        cleanDetails,
        checkInTitle,
        checkInDetails,
        description,
        spaceDescription,
        guestsAccessDescription,
        amenities,
        sleepingArrangements,
        dailyRate
      } = this.state;
      e.preventDefault();

      const rentalData = {
        title,
        subtitle,
        street,
        city,
        country,
        image,
        category,
        bedrooms,
        guests,
        beds,
        bathrooms,
        shared,
        cleanTitle,
        cleanDetails,
        checkInTitle,
        checkInDetails,
        description,
        spaceDescription,
        guestsAccessDescription,
        amenities,
        sleepingArrangements,
        dailyRate
      };

      this.props.createRental(rentalData, this.props.history);
      this.setState({ redirect: true });

      console.log(rentalData);
    } catch (error) {
      this.setState({ errors: this.state.errors });
    }
  };

  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };
  render() {
    const {
      title,
      subtitle,
      street,
      city,
      country,
      image,
      category,
      bedrooms,
      guests,
      beds,
      bathrooms,
      shared,
      cleanTitle,
      cleanDetails,
      checkInTitle,
      checkInDetails,
      description,
      spaceDescription,
      guestsAccessDescription,
      amenities,
      sleepingArrangements,
      dailyRate,
      errors,
      redirect
    } = this.state;

    if (this.state.redirect) {
      return <Redirect to={{ pathname: "/rentals" }} />;
    }

    return (
      <div className="Login">
        <div className="Login__form">
          <div className="row">
            <div className="col-md-5">
              <br />
              <h1 className="Login__title">Log in</h1>
              <br />
              <form onSubmit={this.onSubmit}>
                <label htmlFor="title">Title</label>
                <TextFieldGroup
                  placeholder="Title"
                  name="title"
                  type="text"
                  value={title}
                  onChange={this.onChange}
                  required
                />
                <label htmlFor="subtitle">Subtitle</label>
                <TextFieldGroup
                  placeholder="Subtitle"
                  name="subtitle"
                  type="text"
                  value={subtitle}
                  onChange={this.onChange}
                  required
                />
                <label htmlFor="subtitle">City</label>
                <TextFieldGroup
                  placeholder="City"
                  name="city"
                  type="text"
                  value={city}
                  onChange={this.onChange}
                  required
                />
                <label htmlFor="subtitle">Country</label>
                <TextFieldGroup
                  placeholder="Country"
                  name="country"
                  type="text"
                  value={country}
                  onChange={this.onChange}
                  required
                />

                <label htmlFor="subtitle">Image</label>
                <TextFieldGroup
                  placeholder="Image"
                  name="image"
                  type="file"
                  value={image}
                  onChange={this.onChange}
                  required
                />

                <label htmlFor="subtitle">Category</label>
                <TextFieldGroup
                  placeholder="Category"
                  name="category"
                  type="text"
                  value={category}
                  onChange={this.onChange}
                  required
                />

                <label htmlFor="subtitle">Bedrooms</label>
                <TextFieldGroup
                  placeholder="Bedrooms"
                  name="bedrooms"
                  type="number"
                  value={bedrooms}
                  onChange={this.onChange}
                  required
                />
                <label htmlFor="subtitle">Guests</label>
                <TextFieldGroup
                  placeholder="Guests"
                  name="guests"
                  type="number"
                  value={guests}
                  onChange={this.onChange}
                  required
                />
                <label htmlFor="subtitle">Beds</label>
                <TextFieldGroup
                  placeholder="Beds"
                  name="beds"
                  type="number"
                  value={beds}
                  onChange={this.onChange}
                  required
                />
                <label>Bathrooms</label>
                <TextFieldGroup
                  placeholder="Number of bathrooms"
                  name="bathrooms"
                  type="number"
                  value={bathrooms}
                  onChange={this.onChange}
                  required
                />
                <label>Description</label>
                <TextFieldGroup
                  placeholder="Description"
                  name="description"
                  type="textarea"
                  value={description}
                  onChange={this.onChange}
                  required
                />
                <label>Sleeping Arrangements</label>
                <TextFieldGroup
                  placeholder="Sleeping Arrangements"
                  name="sleepingArrangements"
                  type="text"
                  value={sleepingArrangements}
                  onChange={this.onChange}
                  required
                />

                <label>Daily Rate</label>
                <TextFieldGroup
                  placeholder="Daily Rate"
                  name="dailyRate"
                  type="number"
                  value={dailyRate}
                  onChange={this.onChange}
                  required
                />
                <input
                  type="submit"
                  className="btn btn-submit btn-block mt-4"
                  value="Create"
                />
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  errors: state.errors
});

export default connect(mapStateToProps, { createRental })(RentalForm);
