import axios from "axios";
import setAuthToken from "../utils/setAuthToken";
import jwt_decode from "jwt-decode";

import { FETCH_RENTALS, FETCH_RENTALS_BY_ID, GET_ERRORS } from "./types";

export const fetchRentals = () => async dispatch => {
  // axios.get("http://localhost:8080/api/rentals").then(rentals => {
  //   dispatch({ type: FETCH_RENTALS, rentals });
  // });

  const res = await axios.get("/api/rentals");
  // debugger;
  dispatch({ type: FETCH_RENTALS, payload: res.data });
};

export const fetchRentalsById = rentalId => async dispatch => {
  // axios.get(` http://localhost:8080/api/rentals/${rentalId}`).then(rental => {
  //   dispatch({ type: FETCH_RENTALS_BY_ID, rental });
  // });

  // const rental = rentals.find(rental => rental.id === Number(rentalId));
  // dispatch({ type: FETCH_RENTALS_BY_ID, rental });

  const res = await axios.get(`/api/rentals/${rentalId}`);
  dispatch({ type: FETCH_RENTALS_BY_ID, payload: res.data });
};

// user actions

// export const signupUser = userData => async dispatch => {
//   try {
//     const res = await axios.post("/api/users/signup", { ...userData });
//     dispatch({ payload: res.data });
//   } catch (error) {
//     console.log(error);
//   }
// };

export const register = userData => {
  return axios.post("/api/users/signup", userData).then(
    res => res.data,
    error => Promise.reject(error.response.data.errors)
  );
};

export const registerUser = (userData, history) => async dispatch => {
  try {
    await axios.post("/api/users/signup", userData);
    history.push("/login"); //If we manage to register, we are redirected to login
  } catch (error) {
    dispatch({
      type: GET_ERRORS,
      payload: error.response.data
    });
  }
};

// Login - Get User Token
export const loginUser = userData => async dispatch => {
  try {
    const res = await axios.post("/api/users/login", userData);

    // Save to localStorage
    const { token } = res.data;
    // Set token to ls
    localStorage.setItem("jwtToken", token);
    // Set token to Auth header
    setAuthToken(token);
    // Decode token to get user data
    const decoded = jwt_decode(token);
    // Set current user
    dispatch(setCurrentUser(decoded));
  } catch (error) {
    dispatch({
      type: GET_ERRORS,
      payload: error.response.data
    });
  }
};

// Set logged in user
export const setCurrentUser = decoded => {
  return {
    type: SET_CURRENT_USER,
    payload: decoded
  };
};
