import React, { Component } from "react";
import { Link } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";

import { fetchUserRentals, deleteRental } from "../../actions/";
import { connect } from "react-redux";
import RentalManageModal from "./RentalManageModal";

class RentalManage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      userRentals: [],
      errors: [],
      isFetching: false,
      confirmDelete: false
    };
    this.showDeleteMessage = this.showDeleteMessage.bind(this);
    this.hideDeleteMessage = this.hideDeleteMessage.bind(this);
    this.deleteRental = this.deleteRental.bind(this);
  }

  componentDidMount() {
    this.setState({ isFetching: true });
    this.props.fetchUserRentals();
    this.setState({ isFetching: false });
  }

  showDeleteMessage = () => {
    this.setState({ confirmDelete: true });
  };

  hideDeleteMessage = () => {
    this.setState({ confirmDelete: false });
  };

  deleteRental = (rentalId, rentalIndex) => {
    this.props
      .deleteRental(rentalId)
      .then(() => {
        // this.setState({
        //   userRentals: this.state.userRentals.splice(1, rentalIndex)
        // });
        this.deleteRentalFromList(rentalIndex);
        toast.success("Rental has been succesfuly deleted!");
      })
      .catch(error => toast.error(error));
  };

  deleteRentalFromList(rentalIndex) {
    const userRentals = this.state.userRentals.slice();
    userRentals.splice(rentalIndex, 1);

    this.setState({ userRentals });
  }

  render() {
    const { isAuthenticated } = this.props.auth;
    const { isFetching, confirmDelete } = this.state;
    const { rentals } = this.props;

    const deleteClass = confirmDelete ? "toBeDeleted" : "";

    return (
      <div>
        {isAuthenticated && (
          <section id="userRentals">
            <h1 className="page-title">My Rentals</h1>
            <div className="row">
              {rentals.map((rental, i) => (
                // <p key={i}>{rental.title}</p>
                <div className="col-md-4">
                  <div className={`card text-center ${deleteClass}`}>
                    <div className="card-block">
                      <h4 className="card-title" key={rental._id}>
                        {rental.title} - {rental.city}
                      </h4>
                      <Link className="btn" to={`/rentals/${rental._id}`}>
                        Go to Rental
                      </Link>
                      {rental.bookings && rental.bookings.length > 0 && (
                        <RentalManageModal bookings={rental.bookings} />
                      )}
                    </div>
                    <div className="card-footer text-muted">
                      Created {rental.createdAt}
                    </div>
                    {!confirmDelete && (
                      <button
                        className="btn"
                        style={{ width: "100%", fontWeight: "800" }}
                        onClick={this.showDeleteMessage}
                      >
                        Delete
                      </button>
                    )}
                    {confirmDelete && (
                      <div>
                        <p>Do you confirm ?</p>{" "}
                        <button
                          className="btn"
                          onClick={deleteRental(rental._id, rentalIndex)}
                        >
                          Yes
                        </button>{" "}
                        <button
                          onClick={this.hideDeleteMessage}
                          className="btn"
                          style={{ backgroundColor: "#008489" }}
                        >
                          No
                        </button>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>

            {!isFetching && rentals.length === 0 && (
              <div className="alert alert-warning">
                You dont have any rentals currenty created. If you want
                advertised your property please follow this link.
                <Link
                  style={{ marginLeft: "10px" }}
                  className="btn"
                  to="/rentals/create"
                >
                  Register Rental
                </Link>
              </div>
            )}
          </section>
        )}
      </div>
    );
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
  rentals: state.rentals.rentals
});

export default connect(mapStateToProps, { fetchUserRentals, deleteRental })(
  RentalManage
);
