import { FETCH_PAYMENTS, ACCEPT_PAYMENT } from "../actions/types";

const initialState = {
  payments: []
};

//reducer for payments
export const payments = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_PAYMENTS:
      return { ...state, payments: action.payload };
    case ACCEPT_PAYMENT:
      return { ...state, payments: action.payload };

    default:
      return state;
  }
};
