import React, { Component } from "react";
import { connect } from "react-redux";
import { createRental } from "../../actions/";
import TextFieldGroup from "../common/TextFieldGroup";

export class RentalForm extends Component {
  constructor() {
    super();
    this.state = {
      title: "",
      subtitle: "",
      street: "",
      city: "",
      country: "",
      image: "",
      category: "",
      bedrooms: "",
      guests: "",
      beds: "",
      bathrooms: "",
      shared: true,
      cleanTitle: "",
      cleanDetails: "",
      checkInTitle: "",
      checkInDetails: "",
      description: "",
      spaceDescription: "",
      guestsAccessDescription: "",
      amenities: [],
      sleepingArrangements: "",
      dailyRate: "",
      errors: {}
    };
  }

  onSubmit = e => {
    try {
      this.setState({ errors: {} });
      const {
        title,
        subtitle,
        street,
        city,
        country,
        image,
        category,
        bedrooms,
        guests,
        beds,
        bathrooms,
        shared,
        cleanTitle,
        cleanDetails,
        checkInTitle,
        checkInDetails,
        description,
        spaceDescription,
        guestsAccessDescription,
        amenities,
        sleepingArrangements,
        dailyRate
      } = this.state;
      e.preventDefault();

      const rentalData = {
        title,
        subtitle,
        street,
        city,
        country,
        image,
        category,
        bedrooms,
        guests,
        beds,
        bathrooms,
        shared,
        cleanTitle,
        cleanDetails,
        checkInTitle,
        checkInDetails,
        description,
        spaceDescription,
        guestsAccessDescription,
        amenities,
        sleepingArrangements,
        dailyRate
      };

      this.props.createRental(rentalData);
      console.log(rentalData);
    } catch (error) {
      this.setState({ errors: this.state.errors });
    }
  };

  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };
  render() {
    const {
      title,
      subtitle,
      street,
      city,
      country,
      image,
      category,
      bedrooms,
      guests,
      beds,
      bathrooms,
      shared,
      cleanTitle,
      cleanDetails,
      checkInTitle,
      checkInDetails,
      description,
      spaceDescription,
      guestsAccessDescription,
      amenities,
      sleepingArrangements,
      dailyRate,
      errors
    } = this.state;
    return (
      <div className="Login">
        <div className="Login__form">
          <div className="row">
            <div className="col-md-5">
              <br />
              <h1 className="Login__title">Log in</h1>
              <br />
              <form onSubmit={this.onSubmit}>
                <TextFieldGroup
                  placeholder="Title"
                  name="title"
                  type="text"
                  value={title}
                  onChange={this.onChange}
                />
                <TextFieldGroup
                  placeholder="Subtitle"
                  name="subtitle"
                  type="text"
                  value={subtitle}
                  onChange={this.onChange}
                />
                <input
                  type="submit"
                  className="btn btn-submit btn-block mt-4"
                  value="Login"
                />
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  errors: state.errors
});

export default connect(mapStateToProps, { createRental })(RentalForm);
