import axios from "axios";

import { FETCH_RENTALS, FETCH_RENTALS_BY_ID } from "./types";

export const fetchRentals = () => async dispatch => {
  // axios.get("http://localhost:8080/api/rentals").then(rentals => {
  //   dispatch({ type: FETCH_RENTALS, rentals });
  // });

  const res = await axios.get("/api/rentals");
  // debugger;
  dispatch({ type: FETCH_RENTALS, payload: res.data });
};

export const fetchRentalsById = rentalId => async dispatch => {
  // axios.get(` http://localhost:8080/api/rentals/${rentalId}`).then(rental => {
  //   dispatch({ type: FETCH_RENTALS_BY_ID, rental });
  // });

  // const rental = rentals.find(rental => rental.id === Number(rentalId));
  // dispatch({ type: FETCH_RENTALS_BY_ID, rental });

  const res = await axios.get(`/api/rentals/${rentalId}`);
  dispatch({ type: FETCH_RENTALS_BY_ID, payload: res.data });
};

// user actions

export const signupUser = userData => async dispatch => {
  try {
    const res = await axios.post("/api/users/signup", { ...userData });
    dispatch({ payload: res.data });
  } catch (error) {
    console.log(error);
  }
};

