          <div class="col-xs-12 col-sm-12 col-md-4 d-flex align-items-stretch mb-3">
            <div class="card">
              <img class="card-img" src={rental.image} />

              <div class="card-content">
                <h4>
                  {" "}
                  {rental.title} - {rental.city}
                </h4>
                <Link className="btn" to={`/rentals/${rental._id}`}>
                  Go to your Rental
                </Link>
                {rental.bookings && rental.bookings.length > 0 && (
                  <RentalManageModal bookings={rental.bookings} />
