      }
    }
  };
};

const formStyle = () => {
  return {
    height: "100px",
    backgroundColor: "#fbfbfb",
    padding: "5px",
    marginTop: "15px"
  };
};

class PaymentCheckoutForm extends Component {
  handleSubmit = e => {
